import { useEffect, useState } from "react";
import { db } from "../firebase";
import {
  collection,
  onSnapshot,
  deleteDoc,
  doc,
  query,
  orderBy,
} from "firebase/firestore";

export default function ProductList({ onEdit }) {
  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState("All");
  const [searchTerm, setSearchTerm] = useState("");
  const [page, setPage] = useState(1);
  const pageSize = 5;

  useEffect(() => {
    const q = query(collection(db, "products"), orderBy("createdAt", "desc"));

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const productList = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setProducts(productList);
    });

    return () => unsubscribe();
  }, []);

  const deleteProduct = async (id) => {
    await deleteDoc(doc(db, "products", id));
  };

  // Apply both filter and search
  const filtered = products.filter((p) => {
    const matchesCategory = filter === "All" || p.category === filter;
    const matchesSearch = p.name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const paginated = filtered.slice((page - 1) * pageSize, page * pageSize);

  return (
    <>
     <div
  style={{
    display: "flex",
    justifyContent: "center", // centers the row
    gap: "20px",
    marginBottom: "16px",
  }}
>
  <select
    value={filter}
    onChange={(e) => {
      setFilter(e.target.value);
      setPage(1);
    }}
    style={{
      width: "30%",
      padding: "8px",
      fontSize: "14px",
      borderRadius: "6px",
      border: "1px solid #ccc",
    }}
  >
    <option>All</option>
    <option>Electronics</option>
    <option>Clothing</option>
    <option>Grocery</option>
  </select>

  <input
    type="text"
    placeholder="Search"
    value={searchTerm}
    onChange={(e) => {
      setSearchTerm(e.target.value);
      setPage(1);
    }}
    style={{
      width: "30%",
      padding: "8px",
      fontSize: "14px",
      borderRadius: "6px",
      border: "1px solid #ccc",
    }}
  />
</div>


      <ul style={{
  display: "flex",
  flexWrap: "wrap",
  justifyContent: "center",
  padding: 0,
  margin: "0 auto",
  maxWidth: "900px",
  listStyle: "none",
  gap: "20px",
}}>
  {paginated.map((product) => (
    <li
      key={product.id}
      style={{
        flex: "0 1 45%", // takes around 45% width with space between
        background: "#ffffff",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0 1px 6px rgba(0,0,0,0.1)",
        display: "flex",
        justifyContent: "space-between",
        flexWrap: "wrap",
      }}
    >
      <div className="product-info" style={{ flex: "1 1 70%" }}>
        <h3>{product.name}</h3>
        <p>{product.description}</p>
        <p><strong>Category:</strong> {product.category}</p>
        <p><strong>Price:</strong> â‚¹{product.price}</p>
        <p><strong>In Stock:</strong> {product.inStock ? "Yes" : "No"}</p>
      </div>
      <div className="product-actions" style={{ flex: "1 1 30%", textAlign: "right", display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "flex-end" }}>
      <button style={{ marginBottom: "8px" }} onClick={() => deleteProduct(product.id)}>Delete</button>
<button onClick={() => onEdit(product)}>Edit</button>

      </div>
    </li>
  ))}
</ul>


      {/* Pagination */}
      <div className="pagination">
        {Array.from(
          { length: Math.ceil(filtered.length / pageSize) },
          (_, i) => (
            <button key={i} onClick={() => setPage(i + 1)}>
              {i + 1}
            </button>
          )
        )}
      </div>
    </>
  );
}
