import { useEffect, useState } from "react";
import { db } from "../firebase";
import {
  collection,
  onSnapshot,
  deleteDoc,
  doc,
  query,
  orderBy,
} from "firebase/firestore";
import ProductForm from "./ProductForm"; // ⬅ Import if not already

export default function ProductList() {
  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState("All");
  const [page, setPage] = useState(1);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const pageSize = 5;

  useEffect(() => {
    const q = query(collection(db, "products"), orderBy("createdAt", "desc"));

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const productList = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setProducts(productList);
    });

    return () => unsubscribe();
  }, []);

  const deleteProduct = async (id) => {
    await deleteDoc(doc(db, "products", id));
  };

  const filtered =
    filter === "All"
      ? products
      : products.filter((p) => p.category === filter);

  const paginated = filtered.slice((page - 1) * pageSize, page * pageSize);

  return (
    <>
      {/* Form placed here for edit integration */}
      <ProductForm
        selectedProduct={selectedProduct}
        clearSelection={() => setSelectedProduct(null)}
      />

      <select onChange={(e) => setFilter(e.target.value)}>
        <option>All</option>
        <option>Electronics</option>
        <option>Clothing</option>
        <option>Grocery</option>
      </select>

      <ul>
        {paginated.map((product) => (
          <li key={product.id}>
            <div className="product-info">
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p><strong>Category:</strong> {product.category}</p>
              <p><strong>Price:</strong> ₹{product.price}</p>
              <p><strong>In Stock:</strong> {product.inStock ? "Yes" : "No"}</p>
            </div>
            <div className="product-actions">
              <button onClick={() => deleteProduct(product.id)}>Delete</button>
              <button onClick={() => setSelectedProduct(product)}>Edit</button>
            </div>
          </li>
        ))}
      </ul>

      <div className="pagination">
        {Array.from(
          { length: Math.ceil(filtered.length / pageSize) },
          (_, i) => (
            <button key={i} onClick={() => setPage(i + 1)}>
              {i + 1}
            </button>
          )
        )}
      </div>
    </>
  );
}
